  /**
   * Generic broadcast to subscription channels
   */
  private async broadcast(
    message: ServerMessage,
    channels: Array<{ type: SubscriptionType; targetId: string }>
  ): Promise<void> {
    const targetConnections = new Set<string>();

    // Find all connections subscribed to any of the channels
    for (const channel of channels) {
      const subscriptionKey = `${channel.type}:${channel.targetId}`;
      const subscriptionSet = this.subscriptions.get(subscriptionKey);

      if (subscriptionSet) {
        const subsArray = Array.from(subscriptionSet);
        for (const sub of subsArray) {
          targetConnections.add(sub.connectionId);
        }
      }
    }

    // Send to local connections
    const connectionIdsArray = Array.from(targetConnections);
    for (const connectionId of connectionIdsArray) {
      const ws = this.connections.get(connectionId);
      if (ws && ws.readyState === WebSocket.OPEN) {
        this.sendToConnection(ws, message);
      }
    }

    // Broadcast via Redis for multi-instance coordination
    if (this.redis) {
      const redisMessage: RedisWSMessage = {
        channel: 'ws:broadcast',
        message,
        excludeConnectionId: undefined // Broadcast to all instances
      };

      await this.redis.publish('ws:broadcast', JSON.stringify(redisMessage));
    }

    log(`üì¢ WS: Broadcasted ${message.type} to ${targetConnections.size} connections`);
  }

  /**
   * Handle Redis messages for multi-instance coordination
   */
  private async handleRedisMessage(channel: string, data: string): Promise<void> {
    try {
      if (channel !== 'ws:broadcast') return;

      const redisMessage: RedisWSMessage = JSON.parse(data);

      // Send to specific connections or all if not specified
      const targetConnections = redisMessage.connectionIds || Array.from(this.connections.keys());

      for (const connectionId of targetConnections) {
        if (connectionId === redisMessage.excludeConnectionId) continue;

        const ws = this.connections.get(connectionId);
        if (ws && ws.readyState === WebSocket.OPEN) {
          this.sendToConnection(ws, redisMessage.message);
        }
      }
    } catch (error) {
      log(`‚ùå WS: Redis message handling error: ${error}`);
    }
  }

  /**
   * Send message to specific connection with batching optimization
   */
  private sendToConnection(ws: ExtendedWebSocket, message: ServerMessage): void {
    try {
      if (ws.readyState === WebSocket.OPEN) {
        // PRODUCTION OPTIMIZATION: Batch non-urgent messages for better performance
        if (this.shouldBatchMessage(message)) {
          this.addToBatch(ws, message);
        } else {
          // Send immediately for urgent messages (errors, auth, etc.)
          ws.send(JSON.stringify(message));
        }
      }
    } catch (error) {
      log(`‚ùå WS: Failed to send message to [${ws.connectionId}]: ${error}`);
    }
  }

  /**
   * PRODUCTION OPTIMIZATION: Check if message should be batched
   */
  private shouldBatchMessage(message: ServerMessage): boolean {
    // Batch status updates and progress messages, but not auth/error messages
    return message.type === WSMessageType.TASK_STATUS || 
           message.type === WSMessageType.TASK_PROGRESS ||
           message.type === WSMessageType.TASK_LOGS;
  }

  /**
   * PRODUCTION OPTIMIZATION: Add message to batch queue with size-aware flushing
   */
  private addToBatch(ws: ExtendedWebSocket, message: ServerMessage): void {
    ws.batchedMessages.push(message);

    // Schedule batch send if not already scheduled
    if (!ws.batchTimeout) {
      ws.batchTimeout = setTimeout(() => {
        this.flushBatch(ws);
      }, WebSocketManager.BATCH_TIMEOUT_MS);
    }

    // PRODUCTION OPTIMIZATION: Size-aware flushing - check both count and size limits
    const shouldFlushByCount = ws.batchedMessages.length >= WebSocketManager.BATCH_SIZE_LIMIT;
    const batchSize = this.estimateBatchSize(ws.batchedMessages);
    const shouldFlushBySize = batchSize >= WebSocketManager.BATCH_SIZE_BYTES;

    if (shouldFlushByCount || shouldFlushBySize) {
      log(`üöÄ WS: Force flushing batch [${ws.connectionId}] - count: ${ws.batchedMessages.length}, size: ${batchSize} bytes`);
      this.flushBatch(ws);
    }
  }

  /**
   * PRODUCTION OPTIMIZATION: Estimate batch size in bytes to stay under 64KB limit
   */
  private estimateBatchSize(messages: any[]): number {
    try {
      const batchMessage = {
        type: WSMessageType.BATCH,
        messages,
        batchId: 'size_check',
        count: messages.length,
        totalSize: 0,
        timestamp: new Date().toISOString()
      };
      return JSON.stringify(batchMessage).length;
    } catch (error) {
      // Fallback: estimate roughly 1KB per message
      return messages.length * 1024;
    }
  }

  /**
   * PRODUCTION OPTIMIZATION: Flush batched messages with proper protocol compliance
   */
  private flushBatch(ws: ExtendedWebSocket): void {
    if (ws.batchedMessages.length === 0) return;

    try {
      if (ws.readyState === WebSocket.OPEN) {
        const batchId = `batch_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`;
        const totalSize = this.estimateBatchSize(ws.batchedMessages);

        // PRODUCTION OPTIMIZATION: Send all batched messages in proper BATCH format
        const batchMessage = {
          type: WSMessageType.BATCH,
          messages: ws.batchedMessages,
          batchId,
          count: ws.batchedMessages.length,
          totalSize,
          timestamp: new Date().toISOString()
        };

        ws.send(JSON.stringify(batchMessage));
        log(`üì§ WS: Flushed batch [${ws.connectionId}] - ${ws.batchedMessages.length} messages, ${totalSize} bytes`);

        // Clear batch
        ws.batchedMessages = [];
        if (ws.batchTimeout) {
          clearTimeout(ws.batchTimeout);
          ws.batchTimeout = undefined;
        }
      }
    } catch (error) {
      log(`‚ùå WS: Failed to flush batch for [${ws.connectionId}]: ${error}`);
      // Clear batch anyway to prevent accumulation
      ws.batchedMessages = [];
      if (ws.batchTimeout) {
        clearTimeout(ws.batchTimeout);
        ws.batchTimeout = undefined;
      }
    }
  }

  /**
   * Send error message to connection
   */
  private sendError(ws: ExtendedWebSocket, error: string, code?: string): void {
    const errorMessage = createErrorMessage(error, code);
    this.sendToConnection(ws, errorMessage);
  }

  /**
   * Start heartbeat system
   */
  private startHeartbeat(): void {
    this.heartbeatInterval = setInterval(() => {
      const now = new Date();

      const connectionsArray = Array.from(this.connections.entries());
      for (const [connectionId, ws] of connectionsArray) {
        if (ws.readyState === WebSocket.OPEN) {
          // Check if client has been inactive too long
          const inactiveTime = now.getTime() - ws.lastActivity.getTime();

          if (inactiveTime > this.config.heartbeatTimeout) {
            log(`üíî WS: Closing inactive connection [${connectionId}]`);
            ws.terminate();
            continue;
          }

          // Send ping if needed
          if (inactiveTime > this.config.heartbeatInterval) {
            ws.ping();

            // Set timeout for pong response
            ws.pingTimeout = setTimeout(() => {
              log(`üíî WS: Ping timeout [${connectionId}]`);
              ws.terminate();
            }, this.config.heartbeatTimeout);
          }
        } else {
          // Clean up dead connections
          this.connections.delete(connectionId);
        }
      }
    }, this.config.heartbeatInterval);
  }

  /**
   * Validate subscription permissions
   */
  private async validateSubscriptionPermission(
    agentId: string,
    subscriptionType: SubscriptionType,
    targetId: string
  ): Promise<boolean> {
    try {
      switch (subscriptionType) {
        case SubscriptionType.AGENT:
          // Can only subscribe to own agent
          return targetId === agentId;

        case SubscriptionType.SESSION:
          // Verify session belongs to agent
          const session = await storage.getSessionByAgentId(agentId);
          return session?.id === targetId;

        case SubscriptionType.TASK:
          // Verify task belongs to agent's session
          const task = await storage.getTask(targetId);
          return task?.agentId === agentId;

        default:
          return false;
      }
    } catch (error) {
      log(`‚ùå WS: Permission validation error: ${error}`);
      return false;
    }
  }

  /**
   * Generate unique connection ID
   */
  private generateConnectionId(): string {
    return `ws_${Date.now()}_${Math.random().toString(36).substring(2)}`;
  }

  /**
   * Get connection statistics
   */
  public getStats() {
    return {
      totalConnections: this.connections.size,
      totalSubscriptions: Array.from(this.subscriptions.values()).reduce(
        (sum, set) => sum + set.size, 
        0
      ),
      redisEnabled: !!this.redis
    };
  }

  /**
   * Cleanup resources
   */
  public async shutdown(): Promise<void> {
    log('üîÑ WS: Shutting down WebSocket server...');

    // Clear heartbeat
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
    }

    // Close all connections and cleanup batches
    const connectionsArray = Array.from(this.connections.entries());
    for (const [connectionId, ws] of connectionsArray) {
      // PRODUCTION OPTIMIZATION: Clear batch timeouts before closing
      if (ws.batchTimeout) {
        clearTimeout(ws.batchTimeout);
        ws.batchTimeout = undefined;
      }

      // Flush any pending batched messages before closing
      this.flushBatch(ws);

      ws.close(1000, 'Server shutdown');
    }
    this.connections.clear();
    this.subscriptions.clear();

    // Close WebSocket server
    if (this.wss) {
      this.wss.close();
    }

    // Close Redis connections
    if (this.redis) {
      await this.redis.quit();
    }
    if (this.redisSubscriber) {
      await this.redisSubscriber.quit();
    }

    log('‚úÖ WS: WebSocket server shutdown complete');
  }
}

// Singleton instance
export const wsManager = new WebSocketManager();

shared/websocket-types.ts.content:
import { z } from "zod";

// WebSocket message types
export enum WSMessageType {
  // Client ‚Üí Server messages
  SUBSCRIBE = 'SUBSCRIBE',
  UNSUBSCRIBE = 'UNSUBSCRIBE',
  PING = 'PING',
  AUTHENTICATE = 'AUTHENTICATE',

  // Server ‚Üí Client messages  
  PONG = 'PONG',
  AUTHENTICATED = 'AUTHENTICATED',
  SUBSCRIBED = 'SUBSCRIBED',
  UNSUBSCRIBED = 'UNSUBSCRIBED',

  // Task-related broadcasts
  TASK_STATUS = 'TASK_STATUS',
  TASK_PROGRESS = 'TASK_PROGRESS', 
  TASK_LOGS = 'TASK_LOGS',
  TASK_ERROR = 'TASK_ERROR',

  // Session-related broadcasts
  SESSION_STATUS = 'SESSION_STATUS',
  SESSION_EXPIRED = 'SESSION_EXPIRED',

  // System messages
  CONNECTION_STATUS = 'CONNECTION_STATUS',
  ERROR = 'ERROR',

  // PRODUCTION OPTIMIZATION: Batching support
  BATCH = 'BATCH'
}

// Subscription types
export enum SubscriptionType {
  TASK = 'TASK',           // Subscribe to specific taskId updates
  SESSION = 'SESSION',     // Subscribe to all tasks in a sessionId
  AGENT = 'AGENT'         // Subscribe to all tasks for an agentId
}

// Task status from queue system  
export enum TaskStatus {
  PENDING = 'PENDING',
  PROCESSING = 'PROCESSING', 
  COMPLETED = 'COMPLETED',
  FAILED = 'FAILED'
}

// Task types from queue system
export enum TaskType {
  BROWSER_AUTOMATION = 'BROWSER_AUTOMATION',
  SESSION_START = 'SESSION_START', 
  SESSION_END = 'SESSION_END'
}

// Base WebSocket message interface
export interface WSBaseMessage {
  type: WSMessageType;
  timestamp: string;
  messageId?: string;
}

// Client ‚Üí Server message schemas
export const subscribeMessageSchema = z.object({
  type: z.literal(WSMessageType.SUBSCRIBE),
  subscriptionType: z.nativeEnum(SubscriptionType),
  targetId: z.string(), // taskId, sessionId, or agentId
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const unsubscribeMessageSchema = z.object({
  type: z.literal(WSMessageType.UNSUBSCRIBE),
  subscriptionType: z.nativeEnum(SubscriptionType),
  targetId: z.string(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const authenticateMessageSchema = z.object({
  type: z.literal(WSMessageType.AUTHENTICATE),
  sessionToken: z.string(),
  agentId: z.string(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const pingMessageSchema = z.object({
  type: z.literal(WSMessageType.PING),
  timestamp: z.string(),
  messageId: z.string().optional()
});

// Server ‚Üí Client message schemas
export const taskStatusMessageSchema = z.object({
  type: z.literal(WSMessageType.TASK_STATUS),
  taskId: z.string(),
  sessionId: z.string(),
  agentId: z.string(),
  status: z.nativeEnum(TaskStatus),
  taskType: z.nativeEnum(TaskType),
  progress: z.number().min(0).max(100).optional(),
  timestamp: z.string(),
  messageId: z.string().optional(),
  metadata: z.record(z.any()).optional()
});

export const taskProgressMessageSchema = z.object({
  type: z.literal(WSMessageType.TASK_PROGRESS),
  taskId: z.string(),
  sessionId: z.string(),
  progress: z.number().min(0).max(100),
  stage: z.string().optional(),
  estimatedTimeRemaining: z.number().optional(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const taskLogsMessageSchema = z.object({
  type: z.literal(WSMessageType.TASK_LOGS),
  taskId: z.string(),
  sessionId: z.string(),
  logs: z.array(z.string()),
  logLevel: z.enum(['info', 'warn', 'error', 'debug']).default('info'),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const taskErrorMessageSchema = z.object({
  type: z.literal(WSMessageType.TASK_ERROR),
  taskId: z.string(),
  sessionId: z.string(),
  error: z.string(),
  details: z.record(z.any()).optional(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const sessionStatusMessageSchema = z.object({
  type: z.literal(WSMessageType.SESSION_STATUS),
  sessionId: z.string(),
  agentId: z.string(),
  isActive: z.boolean(),
  expiresAt: z.string(),
  timeRemaining: z.number(), // minutes
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const connectionStatusMessageSchema = z.object({
  type: z.literal(WSMessageType.CONNECTION_STATUS),
  status: z.enum(['connected', 'disconnected', 'reconnecting', 'error']),
  message: z.string().optional(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

export const errorMessageSchema = z.object({
  type: z.literal(WSMessageType.ERROR),
  error: z.string(),
  code: z.string().optional(),
  details: z.record(z.any()).optional(),
  timestamp: z.string(),
  messageId: z.string().optional()
});

// PRODUCTION OPTIMIZATION: Batch message schema for high-throughput scenarios
export const batchMessageSchema = z.object({
  type: z.literal(WSMessageType.BATCH),
  messages: z.array(z.any()), // Array of individual messages
  batchId: z.string(),
  count: z.number(),
  totalSize: z.number(), // Size in bytes for monitoring
  timestamp: z.string(),
  messageId: z.string().optional()
});

// Union type for all client messages
export const clientMessageSchema = z.union([
  subscribeMessageSchema,
  unsubscribeMessageSchema, 
  authenticateMessageSchema,
  pingMessageSchema
]);

// Union type for all server messages  
export const serverMessageSchema = z.union([
  taskStatusMessageSchema,
  taskProgressMessageSchema,
  taskLogsMessageSchema,
  taskErrorMessageSchema,
  sessionStatusMessageSchema,
  connectionStatusMessageSchema,
  errorMessageSchema,
  batchMessageSchema,
  z.object({ type: z.literal(WSMessageType.PONG), timestamp: z.string(), messageId: z.string().optional() }),
  z.object({ type: z.literal(WSMessageType.AUTHENTICATED), timestamp: z.string(), messageId: z.string().optional() }),
  z.object({ type: z.literal(WSMessageType.SUBSCRIBED), subscriptionType: z.nativeEnum(SubscriptionType), targetId: z.string(), timestamp: z.string(), messageId: z.string().optional() }),
  z.object({ type: z.literal(WSMessageType.UNSUBSCRIBED), subscriptionType: z.nativeEnum(SubscriptionType), targetId: z.string(), timestamp: z.string(), messageId: z.string().optional() })
]);

// TypeScript types inferred from schemas
export type SubscribeMessage = z.infer<typeof subscribeMessageSchema>;
export type UnsubscribeMessage = z.infer<typeof unsubscribeMessageSchema>;
export type AuthenticateMessage = z.infer<typeof authenticateMessageSchema>;
export type PingMessage = z.infer<typeof pingMessageSchema>;

export type TaskStatusMessage = z.infer<typeof taskStatusMessageSchema>;
export type TaskProgressMessage = z.infer<typeof taskProgressMessageSchema>;
export type TaskLogsMessage = z.infer<typeof taskLogsMessageSchema>;
export type TaskErrorMessage = z.infer<typeof taskErrorMessageSchema>;
export type SessionStatusMessage = z.infer<typeof sessionStatusMessageSchema>;
export type ConnectionStatusMessage = z.infer<typeof connectionStatusMessageSchema>;
export type ErrorMessage = z.infer<typeof errorMessageSchema>;
export type BatchMessage = z.infer<typeof batchMessageSchema>;

export type ClientMessage = z.infer<typeof clientMessageSchema>;
export type ServerMessage = z.infer<typeof serverMessageSchema>;

// WebSocket connection state
export interface WSConnectionState {
  isConnected: boolean;
  lastPing?: Date;
  lastPong?: Date;
  authenticatedAgentId?: string;
  subscriptions: Set<string>;
  connectionId: string;
}

// Subscription tracking
export interface WSSubscription {
  connectionId: string;
  type: SubscriptionType;
  targetId: string;
  subscribedAt: Date;
}

// Broadcasting channels
export interface BroadcastChannel {
  type: SubscriptionType;
  targetId: string;
}

// WebSocket server configuration
export interface WSServerConfig {
  heartbeatInterval: number; // milliseconds
  heartbeatTimeout: number;  // milliseconds
  maxConnections: number;
  authRequired: boolean;
  enableRedisSync: boolean; // for multi-instance coordination
}

// Redis message for cross-instance coordination
export interface RedisWSMessage {
  channel: string;
  connectionIds?: string[]; // target specific connections, or all if undefined
  message: ServerMessage;
  excludeConnectionId?: string; // exclude sender connection
}

// Default configuration
export const DEFAULT_WS_CONFIG: WSServerConfig = {
  heartbeatInterval: 30000,  // 30 seconds
  heartbeatTimeout: 10000,   // 10 seconds  
  maxConnections: 1000,
  authRequired: true,
  enableRedisSync: true
};

// Utility functions for message creation
export function createTaskStatusMessage(
  taskId: string,
  sessionId: string, 
  agentId: string,
  status: TaskStatus,
  taskType: TaskType,
  progress?: number,
  metadata?: Record<string, any>
): TaskStatusMessage {
  return {
    type: WSMessageType.TASK_STATUS,
    taskId,
    sessionId,
    agentId,
    status,
    taskType,
    progress,
    timestamp: new Date().toISOString(),
    messageId: `task-status-${taskId}-${Date.now()}`,
    metadata
  };
}

export function createTaskProgressMessage(
  taskId: string,
  sessionId: string,
  progress: number,
  stage?: string,
  estimatedTimeRemaining?: number
): TaskProgressMessage {
  return {
    type: WSMessageType.TASK_PROGRESS,
    taskId,
    sessionId,
    progress,
    stage,
    estimatedTimeRemaining,
    timestamp: new Date().toISOString(),
    messageId: `task-progress-${taskId}-${Date.now()}`
  };
}

export function createTaskLogsMessage(
  taskId: string,
  sessionId: string,
  logs: string[],
  logLevel: 'info' | 'warn' | 'error' | 'debug' = 'info'
): TaskLogsMessage {
  return {
    type: WSMessageType.TASK_LOGS,
    taskId,
    sessionId,
    logs,
    logLevel,
    timestamp: new Date().toISOString(),
    messageId: `task-logs-${taskId}-${Date.now()}`
  };
}

export function createErrorMessage(
  error: string,
  code?: string,
  details?: Record<string, any>
): ErrorMessage {
  return {
    type: WSMessageType.ERROR,
    error,
    code,
    details,
    timestamp: new Date().toISOString(),
    messageId: `error-${Date.now()}`
  };
}

