import { Request, Response } from 'express';
import { pool } from './db';
import { logger } from './logger';
import { wsManager } from './websocket';
import { getQueueStats } from './queue';

interface HealthCheck {
  status: 'healthy' | 'unhealthy';
  timestamp: string;
  uptime: number;
  responseTime: number;
  checks: {
    database: 'healthy' | 'unhealthy';
    memory: 'healthy' | 'unhealthy';
    redis?: 'healthy' | 'unhealthy';
    websocket: 'healthy' | 'unhealthy';
    queue: 'healthy' | 'unhealthy';
  };
  metrics: {
    memoryUsage: NodeJS.MemoryUsage;
    wsConnections: number;
    queueStats: {
      waiting: number;
      active: number;
      completed: number;
      failed: number;
      total: number;
    };
  };
  version: string;
}

export async function healthCheck(req: Request, res: Response): Promise<void> {
  const startTime = Date.now();

  const health: HealthCheck = {
    status: 'healthy',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    responseTime: 0, // Will be calculated at the end
    checks: {
      database: 'unhealthy',
      memory: 'healthy',
      websocket: 'healthy',
      queue: 'healthy'
    },
    metrics: {
      memoryUsage: process.memoryUsage(),
      wsConnections: 0,
      queueStats: {
        waiting: 0,
        active: 0,
        completed: 0,
        failed: 0,
        total: 0
      }
    },
    version: process.env.npm_package_version || '1.0.0'
  };

  // Database health check
  try {
    await pool.query('SELECT 1');
    health.checks.database = 'healthy';
  } catch (error) {
    logger.error({ error }, 'Database health check failed');
    health.checks.database = 'unhealthy';
    health.status = 'unhealthy';
  }

  // PRODUCTION OPTIMIZATION: Enhanced health checks with real metrics

  // Memory health check with detailed metrics
  const memUsage = process.memoryUsage();
  health.metrics.memoryUsage = memUsage;
  const memUsageMB = memUsage.heapUsed / 1024 / 1024;
  if (memUsageMB > 500) { // Alert if using more than 500MB
    health.checks.memory = 'unhealthy';
    health.status = 'unhealthy';
  }

  // WebSocket health check
  try {
    if (wsManager && typeof wsManager.getStats === 'function') {
      const wsStats = wsManager.getStats();
      health.metrics.wsConnections = wsStats.totalConnections;
      health.checks.websocket = 'healthy';

      // Consider unhealthy if too many connections
      if (wsStats.totalConnections > 1000) {
        health.checks.websocket = 'unhealthy';
        health.status = 'unhealthy';
      }
    } else {
      console.log('wsManager not available or getStats not a function');
      health.checks.websocket = 'unhealthy';
    }
  } catch (error) {
    console.error('WebSocket health check error:', error);
    logger.error({ error }, 'WebSocket health check failed');
    health.checks.websocket = 'unhealthy';
    health.status = 'unhealthy';
  }

  // Queue health check
  try {
    const queueStats = await getQueueStats();
    health.metrics.queueStats = queueStats;
    health.checks.queue = 'healthy';

    // Consider unhealthy if too many failed jobs
    if (queueStats.failed > 100) {
      health.checks.queue = 'unhealthy';
      health.status = 'unhealthy';
    }
  } catch (error) {
    console.error('Queue health check error:', error);
    logger.error({ error }, 'Queue health check failed');
    health.checks.queue = 'unhealthy';
    health.status = 'unhealthy';
  }

  // Redis health check (if available)
  if (process.env.REDIS_URL) {
    try {
      // We'll implement this when we add Redis back
      health.checks.redis = 'healthy';
    } catch (error) {
      logger.error({ error }, 'Redis health check failed');
      health.checks.redis = 'unhealthy';
      // Don't mark overall as unhealthy for Redis in development
      if (process.env.NODE_ENV === 'production') {
        health.status = 'unhealthy';
      }
    }
  }

  // PRODUCTION OPTIMIZATION: Calculate and include response time in health data
  const responseTime = Date.now() - startTime;
  health.responseTime = responseTime;

  logger.info({
    health,
    responseTime: `${responseTime}ms`
  }, 'Health check completed');

  const statusCode = health.status === 'healthy' ? 200 : 503;
  res.status(statusCode).json(health);
}

// Liveness probe (basic check)
export function livenessCheck(req: Request, res: Response): void {
  res.status(200).json({ 
    status: 'alive',
    timestamp: new Date().toISOString() 
  });
}

// Readiness probe (full check)
export async function readinessCheck(req: Request, res: Response): Promise<void> {
  await healthCheck(req, res);
}

server/logger.ts.content:
// Simple production logger - can be upgraded to pino later
interface LogLevel {
  error: (...args: any[]) => void;
  warn: (...args: any[]) => void;
  info: (...args: any[]) => void;
  debug: (...args: any[]) => void;
}

const createLogger = (component?: string): LogLevel => {
  const prefix = component ? `[${component}]` : '';

  return {
    error: (...args) => console.error(`${new Date().toISOString()} ERROR ${prefix}`, ...args),
    warn: (...args) => console.warn(`${new Date().toISOString()} WARN ${prefix}`, ...args),
    info: (...args) => console.log(`${new Date().toISOString()} INFO ${prefix}`, ...args),
    debug: (...args) => {
      if (process.env.NODE_ENV !== 'production') {
        console.log(`${new Date().toISOString()} DEBUG ${prefix}`, ...args);
      }
    }
  };
};

export const logger = createLogger();

// Security event logger
export const securityLogger = createLogger('security');

// Performance logger
export const performanceLogger = createLogger('performance');

// Add request ID middleware
export const addRequestId = (req: any, res: any, next: any) => {
  req.id = Math.random().toString(36).substring(2, 15);
  req.log = createLogger(`req-${req.id}`);
  next();
};

worker/browser-engine-vnc.ts.content:
/**
 * Enhanced Browser Engine with VNC Live View Integration
 * 
 * Wraps the existing BrowserEngine to support live visual streaming
 * via VNC while maintaining all existing automation capabilities
 */

import { BrowserEngine, BrowserTask, BrowserTaskResult, ExecutionStep } from './browser-engine';
import { VNCManager, VNCSession } from './vnc-manager';
import { EventEmitter } from 'events';

export interface LiveViewConfig {
  enableLiveView: boolean;
  autoStartVNC: boolean;
  resolution: string;
  frameRate: number;
}

export interface LiveBrowserSession {
  sessionId: string;
  vncSession?: VNCSession;
  displayEnv?: string;
  webSocketURL?: string;
  isLiveViewActive: boolean;
}

export class BrowserEngineWithVNC extends EventEmitter {
  private browserEngine: BrowserEngine;
  private vncManager: VNCManager;
  private liveViewConfig: LiveViewConfig;
  private liveSessions = new Map<string, LiveBrowserSession>();

  constructor(config: any, liveViewConfig: Partial<LiveViewConfig> = {}) {
    super();

    this.browserEngine = new BrowserEngine(config);

    this.liveViewConfig = {
      enableLiveView: true,
      autoStartVNC: true,
      resolution: '1280x720',
      frameRate: 30,
      ...liveViewConfig
    };

    this.vncManager = new VNCManager({
      resolution: this.liveViewConfig.resolution,
      enableAuth: true // ‚úÖ SECURITY: Enable authentication for secure VNC access
    });

    // Forward VNC events
    this.vncManager.on('sessionCreated', (vncSession) => {
      this.emit('liveViewStarted', vncSession);
    });

    this.vncManager.on('sessionClosed', (sessionId) => {
      this.emit('liveViewStopped', sessionId);
    });
  }

  /**
   * Initialize the enhanced browser engine with VNC capabilities
   */
  async initialize(): Promise<void> {
    await this.browserEngine.initialize();
    this.log('‚úÖ Enhanced browser engine with VNC initialized');
  }

  /**
   * Start live view for a browser session
   */
  async startLiveView(sessionId: string): Promise<VNCSession | null> {
    if (!this.liveViewConfig.enableLiveView) {
      return null;
    }

    try {
      this.log(`üé• Starting live view for session: ${sessionId}`);

      // Create VNC session
      const vncSession = await this.vncManager.createSession(sessionId);
      const webSocketURL = this.vncManager.getWebSocketURL(sessionId);
      const displayEnv = this.vncManager.getDisplayEnv(sessionId);

      // Set display environment for browser processes
      if (displayEnv) {
        process.env.DISPLAY = displayEnv;
        this.log(`üñ•Ô∏è  Set DISPLAY environment to ${displayEnv}`);
      }

      // Store live session info
      const liveSession: LiveBrowserSession = {
        sessionId,
        vncSession,
        displayEnv: displayEnv || undefined,
        webSocketURL: webSocketURL || undefined,
        isLiveViewActive: true
      };

      this.liveSessions.set(sessionId, liveSession);

      this.log(`‚úÖ Live view started for session ${sessionId}`, {
        vncPort: vncSession.vncPort,
        webSocketPort: vncSession.webSocketPort,
        webSocketURL
      });

      return vncSession;
    } catch (error) {
      this.log(`‚ùå Failed to start live view for session ${sessionId}:`, error);
      return null;
    }
  }

  /**
   * Stop live view for a browser session
   */
  async stopLiveView(sessionId: string): Promise<void> {
    const liveSession = this.liveSessions.get(sessionId);
    if (!liveSession) return;

    try {
      this.log(`üîÑ Stopping live view for session: ${sessionId}`);

      await this.vncManager.closeSession(sessionId);

      liveSession.isLiveViewActive = false;
      this.liveSessions.delete(sessionId);

      this.log(`‚úÖ Live view stopped for session ${sessionId}`);
    } catch (error) {
      this.log(`‚ùå Failed to stop live view for session ${sessionId}:`, error);
    }
  }

  /**
   * Get live view connection details for a session
   */
  getLiveViewDetails(sessionId: string): LiveBrowserSession | null {
    return this.liveSessions.get(sessionId) || null;
  }

  /**
   * Delegate to browser engine while updating VNC activity
   */
  async executeTask(task: BrowserTask): Promise<BrowserTaskResult> {
    // Start live view if enabled and not already active
    if (this.liveViewConfig.enableLiveView && this.liveViewConfig.autoStartVNC) {
      const liveSession = this.getLiveViewDetails(task.sessionId);
      if (!liveSession || !liveSession.isLiveViewActive) {
        await this.startLiveView(task.sessionId);
      }
    }

    // Update VNC session activity
    if (this.liveViewConfig.enableLiveView) {
      this.vncManager.updateActivity(task.sessionId);
    }

    // Execute task with browser engine
    const result = await this.browserEngine.executeTask(task);

    // Add live view metadata to result
    const liveSession = this.getLiveViewDetails(task.sessionId);
    if (liveSession && liveSession.isLiveViewActive) {
      (result as any).liveView = {
        webSocketURL: liveSession.webSocketURL,
        isActive: liveSession.isLiveViewActive,
        vncPort: liveSession.vncSession?.vncPort,
        displayNumber: liveSession.vncSession?.displayNumber
      };
    }

    return result;
  }

  /**
   * Close session with VNC cleanup
   */
  async closeSession(sessionId: string): Promise<void> {
    // Stop live view (browser engine will handle its own cleanup)
    await this.stopLiveView(sessionId);
    this.log(`‚úÖ VNC session closed: ${sessionId}`);
  }

  /**
   * Get all active VNC live sessions
   */
  getActiveLiveSessions(): LiveBrowserSession[] {
    return Array.from(this.liveSessions.values()).filter(session => session.isLiveViewActive);
  }

  /**
   * Enhanced cleanup to include VNC manager
   */
  async cleanup(): Promise<void> {
    this.log('üßπ Cleaning up browser engine with VNC...');

    // Stop all live view sessions
    const sessionIds = Array.from(this.liveSessions.keys());
    await Promise.all(sessionIds.map(id => this.stopLiveView(id)));

    // Cleanup VNC manager
    await this.vncManager.cleanup();

    // Cleanup browser engine (let it handle its own sessions)
    await this.browserEngine.cleanup();

    this.log('‚úÖ Browser engine with VNC cleanup completed');
  }

  /**
   * Get live view statistics
   */
  getLiveViewStats() {
    const vncStats = this.vncManager.getStats();
    const liveSessions = Array.from(this.liveSessions.values());

    return {
      ...vncStats,
      liveSessionsCount: liveSessions.length,
      activeLiveViews: liveSessions.filter(s => s.isLiveViewActive).length,
      liveViewEnabled: this.liveViewConfig.enableLiveView
    };
  }

  /**
   * Toggle live view for a session
   */
  async toggleLiveView(sessionId: string, enable: boolean): Promise<boolean> {
    const liveSession = this.getLiveViewDetails(sessionId);

    if (enable && !liveSession?.isLiveViewActive) {
      const vncSession = await this.startLiveView(sessionId);
      return vncSession !== null;
    } else if (!enable && liveSession?.isLiveViewActive) {
      await this.stopLiveView(sessionId);
      return true;
    }

    return liveSession?.isLiveViewActive || false;
  }

  /**
   * Logging helper
   */
  private log(message: string, data?: any): void {
    const timestamp = new Date().toISOString();
    const logEntry = {
      timestamp,
      component: 'BrowserEngineWithVNC',
      message,
      ...(data && { data }),
    };
    console.log(JSON.stringify(logEntry));
  }
}

worker/vnc-manager.ts.content:
/**
 * VNC Manager for Browser Live View Integration
 * 
 * Leverages Replit's built-in VNC functionality to provide live browser streaming
 * Manages X11 display configuration and VNC session lifecycle
 */

import { EventEmitter } from 'events';
import { spawn, ChildProcess } from 'child_process';
import { existsSync, mkdirSync, writeFileSync } from 'fs';
import { join } from 'path';
import jwt from 'jsonwebtoken';
import { validateJWTToken, DEFAULT_SECURITY_CONFIG } from '../server/security';

export interface VNCConfig {
  displayNumber: number;
  vncPort: number;
  webSocketPort: number;
  resolution: string;
  colorDepth: number;
  enableAuth: boolean;
  password?: string;
}

export interface VNCSession {
  id: string;
  displayNumber: number;
  vncPort: number;
  webSocketPort: number;
  xvfbProcess?: ChildProcess;
  vncProcess?: ChildProcess;
  websockifyProcess?: ChildProcess;
  isActive: boolean;
  createdAt: Date;
  lastActivity: Date;
}

export class VNCManager extends EventEmitter {
  private sessions = new Map<string, VNCSession>();
  private config: VNCConfig;
  private nextDisplayNumber = 1;
  private nextVNCPort = 5901;
  private nextWSPort = 6081;

  constructor(config: Partial<VNCConfig> = {}) {
    super();
    this.config = {
      displayNumber: 1,
      vncPort: 5901,
      webSocketPort: 6081,
      resolution: '1280x720',
      colorDepth: 24,
      enableAuth: true, // ‚úÖ SECURITY: Enable authentication by default
      ...config
    };
  }

  /**
   * Create a new VNC session for browser automation
   */
  async createSession(sessionId: string): Promise<VNCSession> {
    if (this.sessions.has(sessionId)) {
      throw new Error(`VNC session ${sessionId} already exists`);
    }

    const displayNumber = this.nextDisplayNumber++;
    const vncPort = this.nextVNCPort++;
    const webSocketPort = this.nextWSPort++;

    const session: VNCSession = {
      id: sessionId,
      displayNumber,
      vncPort,
      webSocketPort,
      isActive: false,
      createdAt: new Date(),
      lastActivity: new Date()
    };

    try {
      // Check if we're running in Replit environment with built-in VNC
      if (process.env.REPL_ID) {
        this.log(`üì∫ Using Replit's built-in VNC for session ${sessionId}`);
        // In Replit, VNC is automatically available when GUI apps are launched
        session.isActive = true;
        this.sessions.set(sessionId, session);
        this.emit('sessionCreated', session);
        return session;
      }

      // For other environments, set up our own VNC stack
      await this.startXvfb(session);
      await this.startVNCServer(session);
      await this.startWebSocketProxy(session);

      session.isActive = true;
      this.sessions.set(sessionId, session);

      this.log(`‚úÖ VNC session created: ${sessionId} on display :${displayNumber}`);
      this.emit('sessionCreated', session);

      return session;
    } catch (error) {
      this.log(`‚ùå Failed to create VNC session ${sessionId}:`, error);
      await this.cleanupSession(session);
      throw error;
    }
  }

  /**
   * Get VNC connection details for a session
   */
  getSessionDetails(sessionId: string): VNCSession | null {
    return this.sessions.get(sessionId) || null;
  }

  /**
   * Get VNC WebSocket URL for frontend integration
   */
  getWebSocketURL(sessionId: string): string | null {
    const session = this.sessions.get(sessionId);
    if (!session || !session.isActive) return null;

    // In Replit, VNC is accessible via the built-in interface
    if (process.env.REPL_ID) {
      return `${process.env.REPL_SLUG || 'vnc'}.${process.env.REPL_OWNER}.repl.co`;
    }

    return `ws://localhost:${session.webSocketPort}`;
  }

  /**
   * Update session activity timestamp
   */
  updateActivity(sessionId: string): void {
    const session = this.sessions.get(sessionId);
    if (session) {
      session.lastActivity = new Date();
    }
  }

  /**
   * Start Xvfb virtual display (for non-Replit environments)
   */
  private async startXvfb(session: VNCSession): Promise<void> {
    return new Promise((resolve, reject) => {
      const xvfbArgs = [
        `:${session.displayNumber}`,
        '-screen', '0', `${this.config.resolution}x${this.config.colorDepth}`,
        '-ac',
        '+extension', 'GLX',
        '+render',
        '-noreset'
      ];

      session.xvfbProcess = spawn('Xvfb', xvfbArgs);

      session.xvfbProcess.on('error', (error) => {
        this.log(`‚ùå Xvfb failed for session ${session.id}:`, error);
        reject(error);
      });

      // Give Xvfb time to start
      setTimeout(() => {
        if (session.xvfbProcess && !session.xvfbProcess.killed) {
          this.log(`‚úÖ Xvfb started for session ${session.id} on display :${session.displayNumber}`);
          resolve();
        } else {
          reject(new Error('Xvfb failed to start'));
        }
      }, 2000);
    });
  }

  /**
   * Start VNC server (for non-Replit environments)
   */
  private async startVNCServer(session: VNCSession): Promise<void> {
    return new Promise((resolve, reject) => {
      const vncArgs = [
        `-display`, `:${session.displayNumber}`,
        `-rfbport`, session.vncPort.toString(),
        '-shared',
        '-forever',
        '-loop',
        '-noxdamage'
      ];

      // ‚úÖ SECURITY: Add authentication with secure password generation
      if (this.config.enableAuth) {
        const sessionPassword = this.generateSecurePassword(session.id);
        const passwdFile = join('/tmp', `vnc_passwd_${session.id}`);
        writeFileSync(passwdFile, sessionPassword);
        vncArgs.push('-passwd', passwdFile);

        // Store password hash for validation (don't store plaintext)
        const crypto = require('crypto');
        (session as any).passwordHash = crypto.createHash('sha256').update(sessionPassword).digest('hex');

        this.log(`üîê VNC authentication enabled for session ${session.id}`);
      } else {
        vncArgs.push('-nopw');
        this.log(`‚ö†Ô∏è  VNC authentication disabled for session ${session.id}`);
      }

      session.vncProcess = spawn('x11vnc', vncArgs);

      session.vncProcess.on('error', (error) => {
        this.log(`‚ùå VNC server failed for session ${session.id}:`, error);
        reject(error);
      });

      session.vncProcess.stdout?.on('data', (data) => {
        const output = data.toString();
        if (output.includes('PORT=')) {
          this.log(`‚úÖ VNC server started for session ${session.id} on port ${session.vncPort}`);
          resolve();
        }
      });

      // Timeout after 10 seconds
      setTimeout(() => {
        if (session.vncProcess && !session.vncProcess.killed) {
          resolve(); // Assume it started if process is still running
        } else {
          reject(new Error('VNC server failed to start'));
        }
      }, 10000);
    });
  }

  /**
   * Start WebSocket proxy for web-based VNC access
   */
  private async startWebSocketProxy(session: VNCSession): Promise<void> {
    return new Promise((resolve, reject) => {
      const websockifyArgs = [
        session.webSocketPort.toString(),
        `localhost:${session.vncPort}`
      ];

      session.websockifyProcess = spawn('websockify', websockifyArgs);

      session.websockifyProcess.on('error', (error) => {
        this.log(`‚ùå WebSocket proxy failed for session ${session.id}:`, error);
        reject(error);
      });

      session.websockifyProcess.stdout?.on('data', (data) => {
        const output = data.toString();
        if (output.includes('Listen on')) {
          this.log(`‚úÖ WebSocket proxy started for session ${session.id} on port ${session.webSocketPort}`);
          resolve();
        }
      });

      // Timeout after 5 seconds
      setTimeout(() => {
        if (session.websockifyProcess && !session.websockifyProcess.killed) {
          resolve(); // Assume it started if process is still running
        } else {
          reject(new Error('WebSocket proxy failed to start'));
        }
      }, 5000);
    });
  }

  /**
   * Close a VNC session
   */
  async closeSession(sessionId: string): Promise<void> {
    const session = this.sessions.get(sessionId);
    if (!session) return;

    this.log(`üîÑ Closing VNC session: ${sessionId}`);
    await this.cleanupSession(session);
    this.sessions.delete(sessionId);
    this.emit('sessionClosed', sessionId);
  }

  /**
   * Cleanup session processes
   */
  private async cleanupSession(session: VNCSession): Promise<void> {
    session.isActive = false;

    // Kill processes in reverse order
    if (session.websockifyProcess) {
      session.websockifyProcess.kill('SIGTERM');
      session.websockifyProcess = undefined;
    }

    if (session.vncProcess) {
      session.vncProcess.kill('SIGTERM');
      session.vncProcess = undefined;
    }

    if (session.xvfbProcess) {
      session.xvfbProcess.kill('SIGTERM');
      session.xvfbProcess = undefined;
    }

    // Clean up temp files
    try {
      const passwdFile = join('/tmp', `vnc_passwd_${session.id}`);
      if (existsSync(passwdFile)) {
        require('fs').unlinkSync(passwdFile);
      }
    } catch (error) {
      // Ignore cleanup errors
    }
  }

  /**
   * Close all sessions and cleanup
   */
  async cleanup(): Promise<void> {
    this.log('üßπ Cleaning up all VNC sessions...');

    const sessionIds = Array.from(this.sessions.keys());
    await Promise.all(sessionIds.map(id => this.closeSession(id)));

    this.log('‚úÖ VNC manager cleanup completed');
  }

  /**
   * Get DISPLAY environment variable for the session
   */
  getDisplayEnv(sessionId: string): string | null {
    const session = this.sessions.get(sessionId);
    if (!session || !session.isActive) return null;

    return `:${session.displayNumber}`;
  }

  /**
   * List all active sessions
   */
  getActiveSessions(): VNCSession[] {
    return Array.from(this.sessions.values()).filter(s => s.isActive);
  }

  /**
   * Get session statistics
   */
  getStats() {
    const activeSessions = this.getActiveSessions();
    return {
      totalSessions: this.sessions.size,
      activeSessions: activeSessions.length,
      nextDisplayNumber: this.nextDisplayNumber,
      nextVNCPort: this.nextVNCPort,
      nextWSPort: this.nextWSPort
    };
  }

  /**
   * ‚úÖ SECURITY: Validate VNC authentication token
   * Verifies JWT token for WebSocket VNC access
   */
  validateVNCToken(token: string, sessionId: string): { valid: boolean; error?: string; payload?: any } {
    try {
      if (!token || !sessionId) {
        return { valid: false, error: 'Missing token or session ID' };
      }

      // Validate JWT token structure and signature
      const tokenValidation = validateJWTToken(token);
      if (!tokenValidation.valid) {
        this.log(`üö® SECURITY: VNC token validation failed: ${tokenValidation.error}`, { sessionId });
        return { valid: false, error: tokenValidation.error };
      }

      const payload = tokenValidation.payload;

      // Verify token is for VNC access
      if (payload.type !== 'vnc_access') {
        this.log(`üö® SECURITY: Invalid token type for VNC access: ${payload.type}`, { sessionId });
        return { valid: false, error: 'Invalid token type' };
      }

      // Verify session ID matches
      if (payload.sessionId !== sessionId) {
        this.log(`üö® SECURITY: Session ID mismatch in VNC token`, { 
          tokenSessionId: payload.sessionId, 
          requestSessionId: sessionId 
        });
        return { valid: false, error: 'Session ID mismatch' };
      }

      // Check if session exists and is active
      const session = this.sessions.get(sessionId);
      if (!session || !session.isActive) {
        this.log(`üö® SECURITY: VNC access denied - session not found or inactive`, { sessionId });
        return { valid: false, error: 'Session not found or inactive' };
      }

      this.log(`‚úÖ SECURITY: VNC token validated successfully`, { 
        sessionId,
        agentId: payload.agentId,
        tokenExpiration: new Date(payload.exp * 1000).toISOString()
      });

      return { valid: true, payload };
    } catch (error: any) {
      this.log(`‚ùå SECURITY: VNC token validation error: ${error.message}`, { sessionId });
      return { valid: false, error: 'Token validation failed' };
    }
  }

  /**
   * ‚úÖ SECURITY: Generate secure password for VNC session
   * Creates session-specific password for VNC authentication
   */
  generateSecurePassword(sessionId: string): string {
    try {
      // Generate session-specific password using sessionId and secret
      const passwordData = `${sessionId}:${DEFAULT_SECURITY_CONFIG.jwtSecret}:${Date.now()}`;
      const crypto = require('crypto');
      const password = crypto.createHash('sha256').update(passwordData).digest('hex').substring(0, 16);

      this.log(`üîê Generated secure VNC password for session`, { sessionId });
      return password;
    } catch (error: any) {
      this.log(`‚ùå Failed to generate VNC password: ${error.message}`, { sessionId });
      // Fallback to simple session-based password
      return sessionId.substring(0, 12);
    }
  }

  /**
   * ‚úÖ SECURITY: Create authenticated WebSocket URL
   * Generates VNC WebSocket URL with authentication parameters
   */
  createAuthenticatedWebSocketURL(sessionId: string, token: string): string {
    const session = this.sessions.get(sessionId);
    if (!session || !session.isActive) return '';

    // In Replit, VNC is accessible via the built-in interface with auth
    if (process.env.REPL_ID) {
      const baseURL = `${process.env.REPL_SLUG || 'vnc'}.${process.env.REPL_OWNER}.repl.co`;
      return `wss://${baseURL}/vnc?token=${encodeURIComponent(token)}&sessionId=${encodeURIComponent(sessionId)}`;
    }

    // For other environments, use local WebSocket with auth
    return `ws://localhost:${session.webSocketPort}?token=${encodeURIComponent(token)}&sessionId=${encodeURIComponent(sessionId)}`;
  }

  private log(message: string, data?: any): void {
    const timestamp = new Date().toISOString();
    const logEntry = {
      timestamp,
      component: 'VNCManager',
      message,
      ...(data && { data }),
    };
    console.log(JSON.stringify(logEntry));
  }
}

