import { Redis } from "ioredis";
import { logger } from "./logger";

// TTL settings for idempotency keys
const DEFAULT_TTL = 48 * 60 * 60; // 48 hours in seconds (covers Stripe retry timeframe)
const PROCESSING_TTL = 60; // 60 seconds for processing claim
const CLEANUP_INTERVAL = 60 * 60 * 1000; // 1 hour cleanup interval
const REDIS_PREFIX = "stripe_idempotency:";

// Event processing states
type ProcessingState = 'processing' | 'done';

interface EventRecord {
  timestamp: number;
  ttl: number;
  state: ProcessingState;
}

/**
 * Idempotency service for preventing duplicate Stripe webhook processing
 * Uses atomic claim semantics to prevent race conditions
 * PRODUCTION REQUIREMENT: Redis-only operation for Railway deployment
 */
export class IdempotencyService {
  private redis: Redis;
  private cleanupInterval: NodeJS.Timeout | null;

  constructor(redis: Redis) {
    if (!redis) {
      throw new Error('Redis connection is required for idempotency service in production deployment');
    }

    this.redis = redis;
    this.cleanupInterval = null;

    // Start cleanup process for Redis keys
    this.startCleanup();

    logger.info('‚úÖ IDEMPOTENCY: Service initialized (Redis-only)', {
      cleanupInterval: CLEANUP_INTERVAL / 1000 + 's',
      defaultTTL: DEFAULT_TTL / 3600 + 'h'
    });
  }

  /**
   * Atomically claim an event for processing
   * @param eventId - Stripe event ID to claim
   * @param processingTtl - TTL for processing claim in seconds
   * @returns Promise<boolean> - true if successfully claimed, false if already claimed/processed
   */
  async claimEventForProcessing(eventId: string, processingTtl: number = PROCESSING_TTL): Promise<boolean> {
    try {
      const key = `${REDIS_PREFIX}${eventId}`;
      const value = JSON.stringify({ state: 'processing', timestamp: Date.now() });

      // SET key value NX EX - atomic claim operation (Redis-only)
      const result = await this.redis.set(key, value, 'NX', 'EX', processingTtl);

      if (result === 'OK') {
        logger.info('üîí IDEMPOTENCY: Event claimed for processing (Redis)', {
          eventId: eventId.substring(0, 20) + '***',
          processingTtl: processingTtl + 's'
        });
        return true;
      } else {
        // Check if it's already completed vs still processing
        const existingValue = await this.redis.get(key);
        if (existingValue) {
          const parsed = JSON.parse(existingValue);
          logger.info('üîÑ IDEMPOTENCY: Event already claimed/processed (Redis)', {
            eventId: eventId.substring(0, 20) + '***',
            state: parsed.state,
            age: Math.round((Date.now() - parsed.timestamp) / 1000) + 's'
          });
        }
        return false;
      }

    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error claiming event for processing', {
        eventId: eventId.substring(0, 20) + '***',
        error: error.message
      });

      // In case of error, return false to prevent processing
      // This is safer than allowing potential duplicate processing
      return false;
    }
  }

  /**
   * Mark an event as completed (successful processing)
   * Converts from 'processing' state to 'done' state with full TTL
   * @param eventId - Stripe event ID to mark as completed
   * @param ttlSeconds - TTL in seconds (defaults to 48 hours)
   */
  async markEventCompleted(eventId: string, ttlSeconds: number = DEFAULT_TTL): Promise<void> {
    try {
      const timestamp = Date.now();
      const value = JSON.stringify({ state: 'done', timestamp });

      // Update in Redis if available
      if (this.redis) {
        const key = `${REDIS_PREFIX}${eventId}`;
        await this.redis.setex(key, ttlSeconds, value);

        logger.info('‚úÖ IDEMPOTENCY: Event marked as completed (Redis)', {
          eventId: eventId.substring(0, 20) + '***',
          ttl: ttlSeconds + 's'
        });
      }


    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error marking event as completed', {
        eventId: eventId.substring(0, 20) + '***',
        error: error.message
      });
      // Don't throw - failing to mark as completed is better than failing the webhook
    }
  }

  /**
   * Release a processing claim (for error scenarios)
   * Allows the event to be retried by another request
   * @param eventId - Stripe event ID to release
   */
  async releaseEventClaim(eventId: string): Promise<void> {
    try {
      const key = `${REDIS_PREFIX}${eventId}`;
      await this.redis.del(key);

      logger.warn('üîì IDEMPOTENCY: Processing claim released (Redis)', {
        eventId: eventId.substring(0, 20) + '***'
      });

    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error releasing processing claim', {
        eventId: eventId.substring(0, 20) + '***',
        error: error.message
      });
      // Don't throw - cleanup errors shouldn't affect webhook processing
    }
  }

  /**
   * Legacy method for backward compatibility
   * @deprecated Use claimEventForProcessing instead
   */
  async isEventProcessed(eventId: string): Promise<boolean> {
    logger.warn('‚ö†Ô∏è IDEMPOTENCY: Using deprecated isEventProcessed method', {
      eventId: eventId.substring(0, 20) + '***'
    });

    // Check if event exists in either processing or done state (Redis-only)
    try {
      const key = `${REDIS_PREFIX}${eventId}`;
      const exists = await this.redis.exists(key);
      return exists > 0;
    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error in legacy isEventProcessed', {
        eventId: eventId.substring(0, 20) + '***',
        error: error.message
      });
      return false;
    }
  }

  /**
   * Legacy method for backward compatibility
   * @deprecated Use markEventCompleted instead
   */
  async markEventProcessed(eventId: string, ttlSeconds: number = DEFAULT_TTL): Promise<void> {
    logger.warn('‚ö†Ô∏è IDEMPOTENCY: Using deprecated markEventProcessed method', {
      eventId: eventId.substring(0, 20) + '***'
    });

    await this.markEventCompleted(eventId, ttlSeconds);
  }

  /**
   * Get statistics about the idempotency store (Redis-only)
   */
  getStats(): {
    hasRedis: boolean;
    redisConnected: boolean;
  } {
    return {
      hasRedis: true,
      redisConnected: this.redis.status === 'ready'
    };
  }

  /**
   * Start the cleanup process for expired memory entries
   */
  private startCleanup(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    this.cleanupInterval = setInterval(() => {
      this.cleanupExpiredEntries();
    }, CLEANUP_INTERVAL);

    logger.debug('üßπ IDEMPOTENCY: Cleanup process started', {
      interval: CLEANUP_INTERVAL / 1000 + 's'
    });
  }

  /**
   * Clean up expired entries from Redis store
   */
  private async cleanupExpiredEntries(): Promise<void> {
    try {
      // Redis automatically handles TTL expiration, so this is mainly for logging
      const keys = await this.redis.keys(`${REDIS_PREFIX}*`);

      logger.debug('üßπ IDEMPOTENCY: Cleanup cycle completed (Redis auto-expiry)', {
        trackedKeys: keys.length
      });
    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error during cleanup cycle', {
        error: error.message
      });
    }
  }

  /**
   * Shutdown the idempotency service and cleanup resources
   */
  shutdown(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = null;
    }

    logger.info('üõë IDEMPOTENCY: Service shutdown completed (Redis-backed)');
  }

  /**
   * Force cleanup of all expired entries (for testing/debugging)
   */
  async forceCleanup(): Promise<number> {
    try {
      const keys = await this.redis.keys(`${REDIS_PREFIX}*`);
      await this.cleanupExpiredEntries();
      return keys.length; // Return number of tracked keys
    } catch (error: any) {
      logger.error('‚ùå IDEMPOTENCY: Error in force cleanup', {
        error: error.message
      });
      return 0;
    }
  }
}

// Global idempotency service instance
let idempotencyService: IdempotencyService | null = null;

/**
 * Initialize the global idempotency service (Redis required)
 * @param redis - Redis instance (required for production deployment)
 */
export function initializeIdempotencyService(redis: Redis): IdempotencyService {
  if (!redis) {
    throw new Error('Redis connection is required for idempotency service in production deployment');
  }

  if (idempotencyService) {
    // Shutdown existing service
    idempotencyService.shutdown();
  }

  idempotencyService = new IdempotencyService(redis);
  return idempotencyService;
}

/**
 * Get the global idempotency service instance
 * @returns IdempotencyService instance or throws if not initialized
 */
export function getIdempotencyService(): IdempotencyService {
  if (!idempotencyService) {
    throw new Error('Idempotency service not initialized - Redis connection required');
  }
  return idempotencyService;
}

/**
 * Shutdown the global idempotency service
 */
export function shutdownIdempotencyService(): void {
  if (idempotencyService) {
    idempotencyService.shutdown();
    idempotencyService = null;
  }
}

test-concurrent-webhook-race.js.content:
#!/usr/bin/env node

/**
 * Concurrent Webhook Race Condition Test Suite
 * Tests the atomic claim fix for preventing duplicate webhook processing
 */

import crypto from 'crypto';
import axios from 'axios';

const WEBHOOK_URL = process.env.TEST_WEBHOOK_URL || 'http://localhost:5000/api/stripe/webhook';
const WEBHOOK_SECRET = process.env.STRIPE_WEBHOOK_SECRET || 'whsec_test_webhook_secret_for_testing';

console.log('üß™ Starting Concurrent Webhook Race Condition Tests');
console.log(`üìç Testing endpoint: ${WEBHOOK_URL}`);
console.log(`üîë Using webhook secret: ${WEBHOOK_SECRET.substring(0, 10)}...`);

/**
 * Generate a Stripe-compatible webhook signature
 */
function generateStripeSignature(payload, secret, timestamp) {
  const signedPayload = `${timestamp}.${payload}`;
  const signature = crypto
    .createHmac('sha256', secret)
    .update(signedPayload, 'utf8')
    .digest('hex');
  return `t=${timestamp},v1=${signature}`;
}

/**
 * Create a test webhook payload
 */
function createTestWebhookPayload(eventId) {
  const timestamp = Math.floor(Date.now() / 1000);

  return {
    id: eventId,
    object: 'event',
    api_version: '2023-10-16',
    created: timestamp,
    data: {
      object: {
        id: 'pi_test_payment_intent_' + eventId.substring(-8),
        object: 'payment_intent',
        amount: 100,
        currency: 'usd',
        status: 'succeeded',
        customer: 'cus_test_customer_concurrent',
        metadata: {
          agent_session_id: 'test_concurrent_session',
          user_id: 'test_concurrent_user',
          test_type: 'race_condition_test'
        },
        created: timestamp - 60
      }
    },
    livemode: false,
    pending_webhooks: 1,
    request: {
      id: 'req_test_concurrent_' + Math.random().toString(36).substring(2, 8)
    },
    type: 'payment_intent.succeeded'
  };
}

/**
 * Send a single webhook request
 */
async function sendWebhookRequest(eventId, requestId, delayMs = 0) {
  if (delayMs > 0) {
    await new Promise(resolve => setTimeout(resolve, delayMs));
  }

  const payload = JSON.stringify(createTestWebhookPayload(eventId));
  const timestamp = Math.floor(Date.now() / 1000);
  const signature = generateStripeSignature(payload, WEBHOOK_SECRET, timestamp);

  const startTime = Date.now();

  try {
    const response = await axios.post(WEBHOOK_URL, payload, {
      headers: {
        'Content-Type': 'application/json',
        'Stripe-Signature': signature
      }
    });

    const endTime = Date.now();

    return {
      requestId,
      success: true,
      status: response.status,
      data: response.data,
      responseTime: endTime - startTime,
      timestamp: startTime
    };
  } catch (error) {
    const endTime = Date.now();

    return {
      requestId,
      success: false,
      status: error.response?.status || 0,
      data: error.response?.data || null,
      error: error.message,
      responseTime: endTime - startTime,
      timestamp: startTime
    };
  }
}

/**
 * Test 1: Basic Atomic Claim Test
 * Send the same event ID twice in quick succession
 */
async function testBasicAtomicClaim() {
  console.log('\nüî¨ Test 1: Basic Atomic Claim (Same Event ID, Sequential)');

  const eventId = `evt_basic_atomic_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;

  // Send first request
  const result1 = await sendWebhookRequest(eventId, 'req1');
  console.log(`  Request 1: Status ${result1.status}, Processed: ${result1.data?.processed}, Duplicate: ${result1.data?.duplicate}`);

  // Send second request immediately (should be blocked by atomic claim)
  const result2 = await sendWebhookRequest(eventId, 'req2');
  console.log(`  Request 2: Status ${result2.status}, Processed: ${result2.data?.processed}, Duplicate: ${result2.data?.duplicate}`);

  // Verify results
  const success = result1.status === 200 && 
                 result2.status === 200 && 
                 result1.data?.processed === true && 
                 result1.data?.duplicate === false &&
                 result2.data?.duplicate === true;

  console.log(`  ‚úÖ Result: ${success ? 'PASS' : 'FAIL'} - Atomic claim ${success ? 'worked' : 'failed'}`);
  return success;
}

/**
 * Test 2: High Concurrency Race Test
 * Send multiple concurrent requests with same event ID
 */
async function testHighConcurrencyRace() {
  console.log('\nüî¨ Test 2: High Concurrency Race (Same Event ID, Concurrent)');

  const eventId = `evt_race_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
  const concurrentRequests = 5;

  console.log(`  Sending ${concurrentRequests} concurrent requests with same event ID...`);

  // Send multiple concurrent requests
  const promises = [];
  for (let i = 0; i < concurrentRequests; i++) {
    promises.push(sendWebhookRequest(eventId, `concurrent_req_${i + 1}`));
  }

  const results = await Promise.all(promises);

  // Analyze results
  const successfulProcessing = results.filter(r => r.data?.processed === true && r.data?.duplicate === false);
  const duplicateResponses = results.filter(r => r.data?.duplicate === true);
  const totalResponses = results.filter(r => r.status === 200);

  console.log(`  Successful processing: ${successfulProcessing.length}`);
  console.log(`  Duplicate responses: ${duplicateResponses.length}`);
  console.log(`  Total 200 responses: ${totalResponses.length}`);

  // Log individual results
  results.forEach(result => {
    console.log(`    ${result.requestId}: Status ${result.status}, Processed: ${result.data?.processed}, Duplicate: ${result.data?.duplicate}, Time: ${result.responseTime}ms`);
  });

  // Verify atomic behavior: exactly 1 successful processing, rest are duplicates
  const success = successfulProcessing.length === 1 && 
                 duplicateResponses.length === (concurrentRequests - 1) &&
                 totalResponses.length === concurrentRequests;

  console.log(`  ‚úÖ Result: ${success ? 'PASS' : 'FAIL'} - Race condition ${success ? 'prevented' : 'NOT PREVENTED'}`);
  return success;
}

/**
 * Test 3: Rapid Sequential Test
 * Send rapid sequential requests to test timing windows
 */
async function testRapidSequential() {
  console.log('\nüî¨ Test 3: Rapid Sequential (Same Event ID, 10ms intervals)');

  const eventId = `evt_rapid_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
  const sequentialRequests = 4;

  console.log(`  Sending ${sequentialRequests} rapid sequential requests...`);

  const results = [];

  // Send requests with small delays to test timing windows
  for (let i = 0; i < sequentialRequests; i++) {
    const result = await sendWebhookRequest(eventId, `rapid_req_${i + 1}`, i * 10);
    results.push(result);
    console.log(`    Request ${i + 1}: Status ${result.status}, Processed: ${result.data?.processed}, Duplicate: ${result.data?.duplicate}`);
  }

  // Analyze results
  const successfulProcessing = results.filter(r => r.data?.processed === true && r.data?.duplicate === false);
  const duplicateResponses = results.filter(r => r.data?.duplicate === true);

  const success = successfulProcessing.length === 1 && 
                 duplicateResponses.length === (sequentialRequests - 1);

  console.log(`  ‚úÖ Result: ${success ? 'PASS' : 'FAIL'} - Rapid sequential ${success ? 'handled correctly' : 'failed'}`);
  return success;
}

/**
 * Test 4: Different Event IDs Test
 * Verify different events can still process concurrently
 */
async function testDifferentEventIds() {
  console.log('\nüî¨ Test 4: Different Event IDs (Should all process successfully)');

  const baseTimestamp = Date.now();
  const concurrentRequests = 3;

  console.log(`  Sending ${concurrentRequests} concurrent requests with different event IDs...`);

  // Send concurrent requests with different event IDs
  const promises = [];
  for (let i = 0; i < concurrentRequests; i++) {
    const eventId = `evt_different_${baseTimestamp}_${i + 1}`;
    promises.push(sendWebhookRequest(eventId, `different_req_${i + 1}`));
  }

  const results = await Promise.all(promises);

  // Log results
  results.forEach(result => {
    console.log(`    ${result.requestId}: Status ${result.status}, Processed: ${result.data?.processed}, Duplicate: ${result.data?.duplicate}`);
  });

  // Verify all processed successfully (no duplicates)
  const allSuccessful = results.every(r => 
    r.status === 200 && 
    r.data?.processed === true && 
    r.data?.duplicate === false
  );

  console.log(`  ‚úÖ Result: ${allSuccessful ? 'PASS' : 'FAIL'} - Different events ${allSuccessful ? 'processed independently' : 'interfered with each other'}`);
  return allSuccessful;
}

/**
 * Test 5: Stress Test
 * High volume concurrent test
 */
async function testStressTest() {
  console.log('\nüî¨ Test 5: Stress Test (10 concurrent requests, same event ID)');

  const eventId = `evt_stress_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
  const stressRequests = 10;

  console.log(`  Sending ${stressRequests} concurrent requests for stress testing...`);

  // Send high volume concurrent requests
  const promises = [];
  for (let i = 0; i < stressRequests; i++) {
    promises.push(sendWebhookRequest(eventId, `stress_req_${i + 1}`));
  }

  const startTime = Date.now();
  const results = await Promise.all(promises);
  const endTime = Date.now();

  // Analyze results
  const successfulProcessing = results.filter(r => r.data?.processed === true && r.data?.duplicate === false);
  const duplicateResponses = results.filter(r => r.data?.duplicate === true);
  const errors = results.filter(r => !r.success);

  console.log(`  Total time: ${endTime - startTime}ms`);
  console.log(`  Successful processing: ${successfulProcessing.length}`);
  console.log(`  Duplicate responses: ${duplicateResponses.length}`);
  console.log(`  Errors: ${errors.length}`);

  // Log response times
  const responseTimes = results.map(r => r.responseTime);
  const avgResponseTime = responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length;
  console.log(`  Average response time: ${Math.round(avgResponseTime)}ms`);

  const success = successfulProcessing.length === 1 && 
                 duplicateResponses.length === (stressRequests - 1) &&
                 errors.length === 0;

  console.log(`  ‚úÖ Result: ${success ? 'PASS' : 'FAIL'} - Stress test ${success ? 'passed' : 'failed'}`);
  return success;
}

/**
 * Run all tests
 */
async function runAllTests() {
  console.log('üöÄ Starting Concurrent Webhook Race Condition Test Suite\n');

  const tests = [
    testBasicAtomicClaim,
    testHighConcurrencyRace, 
    testRapidSequential,
    testDifferentEventIds,
    testStressTest
  ];

  const results = [];

  for (const test of tests) {
    try {
      const result = await test();
      results.push(result);
    } catch (error) {
      console.error(`‚ùå Test failed with error: ${error.message}`);
      results.push(false);
    }

    // Small delay between tests
    await new Promise(resolve => setTimeout(resolve, 500));
  }

  // Summary
  const passed = results.filter(r => r).length;
  const total = results.length;

  console.log('\nüìä Test Results Summary:');
  console.log(`  Tests passed: ${passed}/${total}`);
  console.log(`  Success rate: ${Math.round((passed / total) * 100)}%`);

  if (passed === total) {
    console.log('\nüéâ ALL TESTS PASSED! Race condition successfully fixed!');
    console.log('‚úÖ Atomic claim semantics working correctly');
    console.log('‚úÖ Concurrent webhook processing prevented');
    console.log('‚úÖ Duplicate detection working properly');
  } else {
    console.log('\n‚ùå SOME TESTS FAILED! Race condition may still exist.');
    console.log('‚ö†Ô∏è  Check implementation of atomic claim semantics');
  }

  return passed === total;
}

// Run tests if called directly
if (import.meta.url === `file://${process.argv[1]}`) {
  runAllTests().catch(error => {
    console.error('üí• Test suite crashed:', error);
    process.exit(1);
  });
}

export { runAllTests };

